import React, {useRef} from 'react';

 class DynamicScrollToTop  extends React.Component() {
    constructor(props) {
        super(props)
        this.myRef = React.createRef()  
    }

    componentDidMount() {
      this.myRef.current.scrollTo(0, 0);
    }

    render() {
        return <div ref={this.myRef}></div> 
    }   
}
