import {useState, useEffect, useRef} from "react";
import "../styles/certificatesStyles/certificates.scss";
import ScrollToTopHook  from './ScrollToTopHook';


const Certificates = () => {
    const nodeCertificate = require("../images/nodeJs_img.JPG");
    const javaCertificate = require("../images/java_img.JPG");
    const [dataSrc, setDataSrc] = useState([nodeCertificate, "Node.js certificate"]);
    
    
    window.onscroll = function() {animateBigSertificate()};    

    
    function animateBigSertificate() {
        if (document.documentElement.scrollTop > 30) {
          document.getElementById("bigCertificate").className = "showBigSertificate";
        } else {
          document.getElementById("bigCertificate").className = "showCertificateContainer";
        }
      }
    return ( 
        <div   onScroll={animateBigSertificate} className = "certificates-container">  
            <p className="certificates-title">Certificates</p>

            <div id="bigCertificate" >
                <img className="showCertificate" src={dataSrc[0]} alt={dataSrc[1]}/>
            </div>
            <ScrollToTopHook />
            <ul className="certificates-list">
                <li className="certificates-item">
                    <img 
                         src={javaCertificate}  
                         alt="Java certificate"
                         onClick = {() => {
                             setDataSrc([javaCertificate, "Java certificate"])}
                         }
                    />
                </li>
                <li className="certificates-item">
                    <img 
                         src={nodeCertificate} 
                         alt="Node.js certificate"
                         onClick = {() => {
                             setDataSrc([nodeCertificate, "Node.js certificate"])}
                         }
                    />
                </li>
            </ul>
        </div>     
    )
}
export default Certificates;
