import React, {useRef} from 'react';

class DynamicScrollToTop extends React.Component() {
    constructor(props) {
        super(props)
    }

    render() {
        return({componentDidMount() {
            window.scrollTo(0, 0)
          }})
    }   
}

export default DynamicScrollToTop
