import React from 'react';

let lastScroll;
let ticking = false;

class DynamicScrollToTop extends React.Component {
  shouldComponentUpdate() {
    window.addEventListener('scroll', this.handleScroll);
  }

  nav = React.createRef();

  handleScroll = () => {
    lastScroll = window.scroll(0, 0);
    if (!ticking) {
      window.requestAnimationFrame(() => {
        this.nav.current.style.top = `${lastScrollY}px`;
        ticking = false;
      });

      ticking = true;
    }
  };

  render() {
    return (
      <div>
        <nav ref={this.nav}>
        </nav>
      </div>
    );
  }
}

export default DynamicScrollToTop;
