let lastScroll;
let ticking = false;

class DynamicScrollToTop extends React.Component {
  shouldComponentUpdate() {
    window.addEventListener('scroll', this.handleScroll);
  }

  nav = React.createRef();

  handleScroll = () => {
    lastScroll = window.scroll(0, 0);
     };

  render() {
    return (
      <div>
        <nav ref={this.nav}>
        </nav>
      </div>
    );
  }
}

export default DynamicScrollToTop;
